import React,{useState} from 'react';
import { ActionFunction, useNavigate } from 'react-router-dom';
import bonjour from 'bonjour';
import "./index.css"

export let UserName:string;

const App:React.FC = ()=>{
  const [Username, setUsername] = useState('');
  const [Password, setPassword] = useState('');
  const [Response, setResponse] = useState(null);
  const [errorMassege, serErrorMassege] = useState(['',"red"]);
  let massege = "< 로그인 >"
  const data = { Username, Password };

  // ---경로이동 매커니즘
  const navigate = useNavigate();

  const chagePageinMain = () => {
    UserName = Username;
    navigate('/Home'); // '/Home' 경로로 이동
  };


  // ----fetch로직 묶음
  let serverURI:string;
  bonjour().find({type:'http'}, service=>{
    if (service.name === "miniGames NodeServer") {
      serverURI = `http://${service.host}:${service.port}`
    }
  }) 

  const fetchDate = (router:string, Data:any, action:Function)=>{
    fetch(serverURI+router, {
      method: 'POST',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/json'
      },
      body: Data,
    })
    .then((res) => res.json())
    .then((result) => action(result))
    .catch((err)=>console.log("ERROR:",err))

  }


  //------데이터 호출로직
  const login = async (e:any) => {
    e.preventDefault();
    fetchDate("/Login", JSON.stringify(data), (result:any)=>{
      if (result === "NotUserData"){
        serErrorMassege(["이름 또는 비밀번호가 잘못되었습니다.","red"])
      } else {
        chagePageinMain()
        serErrorMassege(['',"red"])
      }
      setResponse(result);
    })
  };

  const signUp = async (e:any) => {
    e.preventDefault();
    fetchDate("signUp",JSON.stringify(data),(result:any)=>{
      if (result === "signUp"){
        serErrorMassege(['성공적으로 가입되었습니다.',"green"])
      } else if(result === "inputErr"){
        serErrorMassege(['현제 사용중인 이름입니다.',"red"])
      } else if(result === "spaceErr"){
        serErrorMassege(['이름 또는 비밀번호에 띄어쓰기가 사용되면 안됩니다.',"red"])
      }
    })
  }

  return (
    <>
      <div className='background'>
        <div id="container">
          <h1 id="center">{massege}</h1>
          <form>
            <div className="form-group">
              <h2><label htmlFor="username">Username:</label></h2>
              <input
                id="username"
                name="username"
                type="text"
                value={Username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="form-group">
                <h2><label htmlFor="password">Password:</label></h2>
              <input
                id="password"
                name="password"
                type="password"
                value={Password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="button-group">
              <button className="button" type="submit" onClick={login}>로그인</button>
              <button className="button" id="nextUser" onClick={signUp}>신규가입</button>
            </div>
          </form>
          <div className='error' style={{color:errorMassege[1]}}>{errorMassege[0]}</div>
        </div>
      </div>
    </>
  )
}

export default App;
