import React,{useState} from 'react';


const App:React.FC = ()=>{
  const [Username, setUsername] = useState('');
  const [Password, setPassword] = useState('');
  const [Response, setResponse] = useState(null);
  let errorMassege = ""

  const handleSubmit = async (e:any) => {
    e.preventDefault();
    const data = { Username, Password };
    try {
      const res = await fetch('http://localhost:1234/signUp', {
        method: 'POST',
        mode: 'cors',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      })
      const result = await res.json();
      if (!result){
        errorMassege = "이름 또는 비밀번호가 잘못되었습니다."
      } else {
        errorMassege = ""
      }
      setResponse(result);
      console.log(result)

    } catch (error) {
      console.error('ERROR:', error);
    }
  };
  const massege = "< 로그인 >"

  return (
    <div id="container">
      <h1 id="center">{massege}</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <h2><label htmlFor="username">Username:</label></h2>
          <input
            id="username"
            name="username"
            type="text"
            value={Username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-group">
            <h2><label htmlFor="password">Password:</label></h2>
          <input
            id="password"
            name="password"
            type="password"
            value={Password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="button-group">
          <button className="button" type="submit">로그인</button>
          <button className="button" id="nextUser">신규가입</button>
        </div>
      </form>
      <h2>{errorMassege}</h2>
    </div>
  )
}

export default App;
