import axios from "axios";

export const findServerInNetwork = async () => {
    const ipPart = window.location.hostname.split(".");
    const ipTool = `http://${ipPart[0]}.${ipPart[1]}.${ipPart[2]}`;
    console.log("asdawdwa")
    for(let i = 1; i < 255; i++){
        const testIP = `${ipTool}.${i}:1234`;
        console.log(testIP)

        axios.get(testIP+'/test').then((response) => {
            if (response.data === "Find!") {
                console.log(`Find Server IP!`);

                // fetch(testIP+'/test', {
                //     method:"POST",
                //     mode:"cors",
                //     headers: { 'Content-Type': 'application/json' },
                //     body: testIP
                // }).then((res) => {})
                //     .catch((err) => {})
                return testIP; // 서버의 IP를 반환
            }
        }).catch((err) => {
            
        })
    }
    console.log("Server not found.  :C")
    return null;
}
