import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Main.css"

const Main: React.FC = () => {
    const [data,Setdata] = useState(["",0]);
    const navigate = useNavigate();

    const mainData = async () => {
        const chagePageinMain = (fetch:string) => {
            navigate('/'+fetch); //경로 이동
        };

        try {
            const res = await fetch("http://localhost:1234/getData", {
                method: "POST",
                mode: "cors",
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ request: "getData" }) // JSON 형식으로 수정
            });

            const Data = await res.json();
            
            //-----순위 정렬화--------
            const userArray = Object.keys(Data).map(key => ({
                ...Data[key],
                rank: 0, // 초기 순위
            }));
            
            // 점수에 따라 배열 정렬
            userArray.sort((a, b) => b.Point - a.Point);
            
            // 순위 매기기
            userArray.forEach((user, index) => {
            user.rank = index + 1;
            });
            
            Setdata(userArray)
            console.log(data)

        } catch (err) {
            console.log("ERROR:", err);
        }
    };

    useEffect(() => {
        mainData();
        console.log("Render")
    }, []); // 빈 배열을 두 번째 인자로 전달하여 컴포넌트가 마운트될 때 한 번만 실행되도록 함

    return (
        <div className="background">
            <h1 id="scoreBorde"><pre>
                {data[0]}
                점수:{data[2]}
            </pre></h1>
        </div>
    );
};

export default Main;
