import React,{useState} from 'react';
import { useNavigate } from 'react-router-dom';
import "./index.css"

const App:React.FC = ()=>{
  const [Username, setUsername] = useState('');
  const [Password, setPassword] = useState('');
  const [Response, setResponse] = useState(null);
  const [errorMassege, serErrorMassege] = useState(['',"red"]);
  let massege = "< 로그인 >"
  const data = { Username, Password };

  const navigate = useNavigate();

  const chagePageinMain = () => {
    navigate('/Home'); // '/Home' 경로로 이동
  };

  const login = async (e:any) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:1234/Login', {
        method: 'POST',
        mode: 'cors',
        headers: {  
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      })
      const result = await res.json();
      if (result === "NotUserData"){
        serErrorMassege(["이름 또는 비밀번호가 잘못되었습니다.","red"])
      } else {
        chagePageinMain()
        serErrorMassege(['',"red"])
      }
      setResponse(result);

    } catch (error) {
      console.error('ERROR:', error);
    }
  };

  const signUp = async (e:any) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:1234/signUp", {
        method: "POST",
        mode: "cors",
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      })
      const result = await res.json();

      if (result === "signUp"){
        serErrorMassege(['성공적으로 가입되었습니다.',"green"])
      } else if(result === "inputErr"){
        serErrorMassege(['현제 사용중인 이름입니다.',"red"])
      } else if(result === "spaceErr"){
        serErrorMassege(['이름 또는 비밀번호에 띄어쓰기가 사용되면 안됩니다.',"red"])
      }

    } catch(err){
      console.log("ERROR:",err)
    }
  }

  return (
    <>
      <div className='background'>
        <div id="container">
          <h1 id="center">{massege}</h1>
          <form>
            <div className="form-group">
              <h2><label htmlFor="username">Username:</label></h2>
              <input
                id="username"
                name="username"
                type="text"
                value={Username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="form-group">
                <h2><label htmlFor="password">Password:</label></h2>
              <input
                id="password"
                name="password"
                type="password"
                value={Password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="button-group">
              <button className="button" type="submit" onClick={login}>로그인</button>
              <button className="button" id="nextUser" onClick={signUp}>신규가입</button>
            </div>
          </form>
          <div className='error' style={{color:errorMassege[1]}}>{errorMassege[0]}</div>
        </div>
      </div>
    </>
  )
}

export default App;
