import React, { useRef, useEffect } from 'react';
import "./MainInputUI.css";

export let UserName: string;

const MainInputUI: React.FC = () => {
  const hiddenInput = useRef<HTMLInputElement>(null);
  const displayText = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    // 페이지 로드 시 입력 필드에 포커스 설정
    const handleLoad = () => {
      hiddenInput.current?.focus();
    };

    // 입력 필드가 포커스를 잃지 않도록 설정
    const handleClick = () => {
      hiddenInput.current?.focus();
    };

    // 입력 이벤트 처리
    const handleInput = () => {
      if (displayText.current && hiddenInput.current) {
        displayText.current.textContent = hiddenInput.current.value;
      }
    };

    window.addEventListener('load', handleLoad);
    document.addEventListener('click', handleClick);
    hiddenInput.current?.addEventListener('input', handleInput);

    // 클린업 함수로 이벤트 리스너 제거
    return () => {
      window.removeEventListener('load', handleLoad);
      document.removeEventListener('click', handleClick);
      hiddenInput.current?.removeEventListener('input', handleInput);
    };
  }, []);

  return (
    <div className='container'>
      <div className='textBox' ref={displayText}></div>
      <input type="text" className="hidden-input" ref={hiddenInput} />
    </div>
  );
};

export default MainInputUI;
