import React,{useState} from 'react';


const App:React.FC = ()=>{
  const [Username, setUsername] = useState('');
  const [Password, setPassword] = useState('');
  const [Response, setResponse] = useState(null);
  const [errorMassege, serErrorMassege] = useState<string>('');
  let massege = "< 로그인 >"

  const login = async (e:any) => {
    e.preventDefault();
    massege = "< 로그인 >"
    const data = { Username, Password };
    try {
      const res = await fetch('http://localhost:1234/signUp', {
        method: 'POST',
        mode: 'cors',
        headers: {  
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      })
      const result = await res.json();
      if (result === "NotUserData"){
        serErrorMassege("이름 또는 비밀번호가 잘못되었습니다.")
      } else {    
        serErrorMassege('')
      }
      setResponse(result);
      console.log(result)

    } catch (error) {
      console.error('ERROR:', error);
    }
  };

  const signUp = async (e:any) => {
    e.perventDefault();
    massege = "< 회원가입 >"
  }

  return (
    <>
      <div id="container">
        <h1 id="center">{massege}</h1>
        <form>
          <div className="form-group">
            <h2><label htmlFor="username">Username:</label></h2>
            <input
              id="username"
              name="username"
              type="text"
              value={Username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-group">
              <h2><label htmlFor="password">Password:</label></h2>
            <input
              id="password"
              name="password"
              type="password"
              value={Password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="button-group">
            <button className="button" type="submit" onClick={login}>로그인</button>
            <button className="button" id="nextUser" onClick={signUp}>신규가입</button>
          </div>
        </form>
        <div className='error'>{errorMassege}</div>
      </div>
    </>
  )
}

export default App;
