import React,{useRef, useEffect} from 'react'; 
import "./MainInputUI.css"

export let UserName:string;

const MainInputUI:React.FC = ()=>{
  const hiddenInput = useRef<HTMLInputElement>(null)
  const displayText = useRef<HTMLDivElement | null>(null)

  useEffect(()=>{
    window.onload = () => {
      hiddenInput.current?.focus();
    };

    document.addEventListener('click', (event) => {
      hiddenInput.current?.focus();
    });
  },[])

  const input = () => {
    if (displayText.current && hiddenInput.current) {
      displayText.current.textContent = hiddenInput.current.value;
    }
  };

  return (
    <div className='container'> 
      <div className='textBox' ref={displayText}></div>
      <input type="text" className="hidden-input" ref={hiddenInput} onInput={input}/>
      <h1>ㅁㄴㅇㄹ</h1>
    </div>
  )
} 

export default MainInputUI;
